import React from 'react'
import Board from './Board.js'
import { useState, useEffect, useContext } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';

function Homepage() {
    const [name, setName] = useState('')
    const [elo, setELO] = useState('')
    const [timeControl, setTime] = useState(0)

    const changeName = (event) => {
        setName(event.target.value)
    } 

    const changeELO = (event) => {
        setELO(event.target.value)
    }



    return(
        <body style={{backgroundColor: 'grey', height: '100vh', width: '100vw', top: '10vh', position: 'absolute'}}>
            <div style={{fontSize: '144px', textAlign: 'center'}}>CHESS GAME</div>
            <div>
                <div style={{textAlign: 'center'}}>
                    <div>Enter Your Name:</div>
                    <input type="text" onChange={changeName}></input>
                    <div>Enter Your ELO</div>
                    <input type="text" onChange={changeELO}></input>
                    <br/>
                    <br/>
                    <Link to={{
                        pathname: '/play',
                        state: [{name: name, elo: elo, player: 'white'}]
                    }}>
                        <input type="button" value="Play as White!"/>
                    </Link>
                    &nbsp;
                    <Link to={{
                        pathname: '/play',
                        state: [{name: name, elo: elo, player: 'black'}]
                    }}>
                        <input type="button" value="Play as Black!"/>
                    </Link>
                </div>
            </div>
        </body>
    );
}

export default Homepage;
