import React from 'react';
import $ from 'jquery'
import Chessboard from 'chessboardjsx'
import ChessBoard from './ChessBoard.js'
import Chess from 'chess.js'
import { useState, useEffect, useContext } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';

function Board(props) {
    const [selected, setSelected] = useState('x')
    const [position, setPosition] = useState('start')
    const [chess, setChess] = useState(new Chess())
    const [squareStyles, setSquareStyles] = useState({})
    const [moves, setMoves] = useState([])
    const [positions, setPositions] = useState(['start'])
    const [moveable, setMoveable] = useState('false')


    const changePosition = (index) => {
        console.log(positions);
        console.log(positions[index]);
        setPosition(positions[index]);
        if(positions[index] === positions[positions.length - 1])
            setMoveable('true')
        else
            setMoveable('false')
    }

    const convertToTuples = (array) => {
        //[a, b, c, d, e] => [[a, b], [c, d], [e]]
        var newArray = [];
        for(var i = 0; i < array.length; i=i+2) {
            if(i >= array.length)
                break;
            else if(i + 1 < array.length) {
                newArray.push([array[i], array[i+1]])
            }
            else {
                newArray.push([array[i]])
            }
        }
        return newArray;
    }
    const move = (theMove) => {
        if(moveable) {
            console.log(theMove)
            chess.move({from: theMove.sourceSquare, to: theMove.targetSquare})
            setMoves(convertToTuples(chess.history()))
            setPosition(chess.fen())
            var newArray = positions
            newArray.push(chess.fen())
            setPositions(newArray)
        }
    }

    const movePiece = (square) => {
        if(moveable) {
            if(chess.get(square) && chess.get(square).color === chess.turn()) {
                setSelected(square)
                let highlightedSquares = {};
                highlightedSquares[square] = 
                {
                    backgroundColor: "#0000ff"
                }
                let moves = chess.moves({
                    square: square,
                    verbose: true
                });
                if (moves.length === 0) {
                    setSelected('x');
                    setSquareStyles({})
                    return;
                }

                let squaresToHighlight = [];
                for (var i = 0; i < moves.length; i++) {
                    squaresToHighlight.push(moves[i].to);
                }
                for(var i = 0; i < squaresToHighlight.length; i ++) {
                    highlightedSquares[squaresToHighlight[i]] = 
                    {
                        background:
                        "radial-gradient(circle, #0000ff 20%, transparent 40%)",
                        borderRadius: "50%"
                    }
                }
                setSquareStyles(highlightedSquares)
            }
            else if(selected !== 'x') {
                chess.move({from: selected, to: square})
                setMoves(convertToTuples(chess.history()))
                var newArray = positions
                newArray.push(chess.fen())
                setPositions(newArray)
                changePosition(newArray.length - 1)
                setSelected('x')
                setSquareStyles({})
            }
        }
    }

    useEffect(() => {

    })
    return(
        <body>
            <div>
                <ChessBoard orientation={props.color} width={600} position={position} onDrop={move} onSquareClick={movePiece} squareStyles={squareStyles}/>                 
            </div>
            <div style={{overflow: 'scroll', display: 'inline-block', height: '600px', width: '200px', position: 'absolute', top: '20px', right: '-40px', color: 'white', backgroundColor: 'black'}}>
                <div style={{width: 'auto', textAlign: 'center'}}>Game Moves</div>
                <br/>
                {
                    moves.map((move, i) => (
                        <p style={{display: 'flex', justifyContent: 'space-between', width: '160px'}}>
                            <span style={{position: 'absolute', left: '0px'}}>&nbsp;{i+1}</span>
                            <span onClick={() => changePosition(2*i + 1)} style={{position: 'absolute', left: '40px'}}>{move[0]}</span>
                            <span onClick={() => changePosition(2*i + 2)} style={{position: 'absolute', left: '120px'}}>{move[1]}</span>
                            <br/>
                        </p>
                    ))
                }
            </div>
            <div style={{backgroundColor: 'white', width: '200px', position: 'absolute', top: ''}}>

            </div>
        </body>
    );
}

export default Board;
